package practicsetest;

import java.util.Scanner;

public class sequencechar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	String a= "Sanjana";
	int c=a.length();
	char b[]=new char[c];
	for(int i=0;i<c;i++)
	{
		b[i]=a.charAt(i);
		System.out.print(b[i]+",");
	}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		

	}

}
