package practicsetest;

import java.util.Scanner;

public class star {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int d=1;
		for(int a=1;a<=4;a++)
		{
			for(int b=1;b<=a;b++)
			{
				System.out.print(d);
				d++;
			}
			System.out.println();
		}
		
	}

}
