package starproject;

public class class4qudrant {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


	for(int a=5;a>=1;a--)
	{
		for(int c=4;c>=a;c--)
		{
			System.out.print(" ");
		}
		for(int b=1;b<=a;b++)
		{
			System.out.print("*");
		}
		System.out.println();
	}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
